#%% Define Token class

class TokenScrape:
    
    def __init__(self, wbnb_address=WBNB_ADDRESS,
                 pan_routerv2_address=PAN_ROUTERV2_ADDRESS,
                 pan_routerv2_abi=PAN_ROUTERV2_ABI,
                 token_abi=TOKEN_ABI):
        
        bsc = "https://bsc-dataseed.binance.org/"
        self.web3 = Web3(Web3.HTTPProvider(bsc))
        self.wbnb_address = self.web3.toChecksumAddress(wbnb_address)
        self.token_abi = token_abi

        pan_routerv2_address = self.web3.toChecksumAddress(PAN_ROUTERV2_ADDRESS)
        self.pan_routerv2_contract = self.web3.eth.contract(address=pan_routerv2_address, 
                                                          abi=pan_routerv2_abi)

        self.user_agent = "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/63.0.3239.132 Safari/537.36"
        self.referer = "https://bscscan.com/"
        
        
    def getPriceFromPancake(self, token_info):
        """
        Get price from Pancakeswap using Router V2

        """
        address = token_info['address']
        decimal = token_info['decimal']
        
        # Get price in BNB
        token_amount_in = 1
        amount_out = self.pan_routerv2_contract.functions.getAmountsOut(token_amount_in, 
                    [self.wbnb_address, address]).call()
        if 0 in amount_out:
            amount_out = self.pan_routerv2_contract.functions.getAmountsOut(token_amount_in, 
                        [address, self.wbnb_address]).call()
            price_bnb = (amount_out[1]/10**18)/(amount_out[0]/10**decimal)
        else:
            price_bnb = (amount_out[0]/10**18)/(amount_out[1]/10**decimal)
        
        
        # Get BNB price in USD
        url = 'https://api.pancakeswap.info/api/v2/tokens/{}'.format(self.wbnb_address)        
        
        headers = {
            'User-Agent': self.user_agent,
            'Referer': self.referer
        }
        response = requests.get(url, headers).json()
        bnb_price = float(response['data']['price'])
        
        price_usd = price_bnb*bnb_price
        
        return (price_bnb, price_usd)
    
    
    def getAdditionalInfo(self, token_address):
        """
        Get additional info:
            - n_holders: number of holders
            - n_transactions: number of transactions

        """
        headers = {
            'User-Agent': self.user_agent,
            'Referer': self.referer
        }
        url = 'https://bscscan.com/token/{}'.format(token_address)
        response = requests.get(url, headers=headers)
        soup = BeautifulSoup(response.text, 'html.parser')
        text = soup.get_text()
        
        holder_element = re.search(r'Holders:[\n\t\v]*(.*?)addresses', text).group(1)
        holder_element = re.sub(r'[^0-9]', '', holder_element)
        n_holders = int(holder_element.strip())
        
        url = 'https://bscscan.com/txs?a={}'.format(token_address)
        response = requests.get(url, headers=headers)
        soup = BeautifulSoup(response.text, 'html.parser')
        text = soup.get_text()
        
        transaction_element = re.search(r'A total of[\n\t\v]*(.*?)transaction', text).group(1)
        transaction_element = re.sub(r'[^0-9]', '', transaction_element)
        n_transactions = int(transaction_element.strip())
        
        return (n_holders, n_transactions)
    
    
    def getOneTokenInfo(self, token_address):
        token_address = self.web3.toChecksumAddress(token_address)
        token_contract = self.web3.eth.contract(address=token_address, abi=self.token_abi)
        token_info = dict()
        token_info['time_unix'] = time.time()
        token_info['address'] = token_address
        token_info['name'] = token_contract.functions.name().call()
        token_info['symbol'] = token_contract.functions.symbol().call()
        token_info['decimal'] = token_contract.functions.decimals().call()
        token_info['supply'] = token_contract.functions.totalSupply().call()
        
        n_holders, n_transactions = self.getAdditionalInfo(token_address)
        token_info['num_holders'] = n_holders
        token_info['num_transactions'] = n_transactions
        
        price_bnb, price_usd = self.getPriceFromPancake(token_info)
        token_info['price_bnb'] = price_bnb
        token_info['price_usd'] = price_usd
        
        return token_info
    
    
    def run(self, token_addresses):
        token_infos = []
        for token_address in token_addresses:
            token_info = self.getOneTokenInfo(token_address)
            token_infos.append(token_info)
        return token_infos


#%%

if __name__ == '__main__':
    
    with open(input_file, 'r') as file:
        addresses = [line.strip() for line in file]
        
    token_infos = TokenScrape().run(addresses)
    token_df = pd.DataFrame(token_infos)
    token_df.to_csv('output.csv', index=False)
